import React, { useState } from 'react';
import functions from "./functionModules";
import dictionaries from "./API's";

function SearchDefinition() {
    const [dictionaryDefinitions, setDictionaryDefinitions] = useState([]);
    const handleSubmit = async (e) => {
        e.preventDefault();

        const wordToSearch = e.target.querySelector('input[id="search-bar"]').value;
        const defaultDictionaryDefinitions = await dictionaries.getWordDefaultDictionary(wordToSearch)
        setDictionaryDefinitions([{
            'dictionaryName': 'Default Dictionary', 'dictionary': defaultDictionaryDefinitions
        }]);
    }
    
    function ShowDefinitions() {
        const [collapsedDicts, setCollapsedDicts] = useState([]); // State to track collapsed dictionaries

        const handleCollapse = (index) => {
            setCollapsedDicts((prevCollapsed) => {
                const newCollapsed = [...prevCollapsed];
                newCollapsed[index] = !newCollapsed[index]; // Toggle the collapse state
                return newCollapsed;
            });
        };

        try {
            const dictionaryDivs = dictionaryDefinitions.map((dictionary, index) => {
                const definitionHeaders = [];
                for (const [meaning, definitions] of Object.entries(dictionary.dictionary)) {
                    definitions[0].forEach((definition, i) => {
                        definitionHeaders.push(
                            <h3 key={i} className={"definition-header"}>{definition.definition}</h3>
                        );
                    });
                }

                return (
                    <div key={index} className={"dictionary-div"}>
                        <button
                            type={"button"}
                            className={"collapse-button"}
                            onClick={() => handleCollapse(index)}
                        >
                            {dictionary.dictionaryName}
                        </button>
                        <div className={`dictionary-div-content ${collapsedDicts[index] ? 'collapsed' : ''}`}>
                            {definitionHeaders}
                        </div>
                    </div>
                );
            });

            return dictionaryDivs;
        } catch (error) {
            return <h3>{`${error}`}</h3>;
        }
    }


    return (
        <div id={"definition-container"}>
            <form onSubmit={handleSubmit} action="#">
                <input
                    id="search-bar"
                    className="search-input"
                    placeholder="Search"
                />
                <input type="submit" value={"Search"} className={"submit-button"}/>
            </form>
            {/*{definitions.length !== 0 ? response : nothing}*/}
            <div id={"definition-div"}>
                <ShowDefinitions />
            </div>
        </div>
    );
}


const components = {searchBar: SearchDefinition}
export default components;
