import React, { useState } from 'react';
import functions from "./functionModules";
import dictionaries from "./API's";

function SearchDefinition() {
    const [definitions, setDefinitions] = useState([]);
    const handleSubmit = async (e) => {
        e.preventDefault();

        const wordToSearch = e.target.querySelector('input[id="search-bar"]').value;
        const newDefinitions = await dictionaries.getWordDefaultDictionary(wordToSearch)
        console.log(newDefinitions)
        setDefinitions([newDefinitions]);
        console.log(definitions)
    }

    const response = (
        <h3>{definitions[0]}</h3>
    )

    function FunnyStuff() {
        const rows = []
        definitions.forEach((definition) => {
            rows.push(
                <h3>{definition}</h3>
            )
        })

        return rows
    }

    const nothing = <h3>{`${definitions.length}`}</h3>

    return (
        <div>
            <form onSubmit={handleSubmit} action="#">
                <input
                    id="search-bar"
                    className="search-input"
                    placeholder="Search"
                />
                <input type="submit" />
            </form>
            {definitions.length !== 0 ? response : nothing}
            {FunnyStuff}
        </div>
    );
}


const components = {searchBar: SearchDefinition}
export default components;
