import functions from "./functionModules";
import dictionaries from "./API's";

function searchBar(searchState) {
    const handleSubmit = (e) => {
        e.preventDefault();
        try {
            switch (searchState) {
                case 'definition':
                    functions.updateText("response-text", document.getElementById("search-input").value())
            }
        } catch (error) {
            console.log(error);
        }
    }

    return (
        <form onSubmit={handleSubmit}>
            <input
                id={"search-bar"}
                className={"search-input"}
                placeholder={"Search"}
            />
            <input type={"submit"}/>
        </form>
    )
}

const components = {searchBar}
export default components;
